mapper.insert:
INSERT INTO reservation (
    store_id, user_name, rsrv_dt, rsrv_person, amount,
    rsrv_req, rsrv_create_time, store_name, store_addr, rsrv_status, rsrv_number
) VALUES (
             #{storeId}, #{userName}, #{rsrvDt}, #{rsrvPerson}, #{amount},
             #{rsrvReq}, NOW(), #{storeName}, #{storeAddr}, '결제 중', #{rsrvNumber}
         );

INSERT INTO reservation (
    store_id, user_name, rsrv_dt, rsrv_person,
    rsrv_req, store_name, store_addr, rsrv_status, rsrv_number
) VALUES (
             #{storeId}, #{userName}, #{rsrvDt}, #{rsrvPerson},
             #{rsrvReq}, #{storeName}, #{storeAddr}, '전화 예약', #{rsrvNumber}
         );

mapper.update:
UPDATE reservation
SET rsrv_status = #{rsrvStatus}
WHERE rsrv_no = #{rsrvNo};

UPDATE reservation
SET payment_id = #{paymentId}
WHERE rsrv_no = #{rsrvNo};

UPDATE reservation
SET reason = #{reason}
WHERE rsrv_no = #{rsrvNo};

mapper.select:
SELECT SUM(rsrv_person)
FROM reservation
WHERE rsrv_dt = #{rsrvDt}
  AND store_id = #{storeId}
  AND rsrv_status IN ('예약 확정', '전화 예약', '예약 요청');

SELECT SUM(rsrv_person)
FROM reservation
WHERE DATE(rsrv_dt) = DATE(#{rsrvDt})
  AND store_id = #{storeId}
  AND rsrv_status IN ('예약 확정', '전화 예약', '예약 요청');

SELECT COUNT(*)
FROM reservation
WHERE user_name = #{userName}
AND DATE(rsrv_dt) > NOW()
AND rsrv_status IN ('예약 확정', '예약 요청');

SELECT COUNT(*)
FROM reservation
WHERE store_id = #{storeId}
  AND rsrv_dt > NOW()
  AND amount <> 0
  AND rsrv_status IN ('예약 확정', '예약 요청');

SELECT COUNT(*)
FROM reservation
WHERE store_id = #{storeId}
  AND rsrv_dt > NOW()
  AND rsrv_status = '전화 예약';

SELECT rsrv_no, rsrv_status, rsrv_number, rsrv_person, rsrv_req, rsrv_dt, user_name, store_name, store_addr
FROM reservation
WHERE rsrv_status <> '결제 중';

SELECT rsrv_no, rsrv_status, rsrv_number, rsrv_person, rsrv_req, rsrv_dt, store_name
FROM reservation
WHERE user_name = #{userName}
  AND rsrv_dt >= NOW()
  AND rsrv_status IN ('예약 요청', '예약 확정', '이용 완료', '리뷰 완료', '예약 취소', '예약 거절');

SELECT rsrv_no, rsrv_status, rsrv_number, rsrv_person, rsrv_req, rsrv_dt
FROM reservation
WHERE store_id = #{storeId}
  AND rsrv_dt >= NOW()
  AND rsrv_status IN ('예약 요청', '예약 확정', '이용 완료', '리뷰 완료', '전화 예약');
